sp_widget.script:
(function() {
	var chUtil = new ChameleonUtil();
	var validFields = ['CallServiceDeskDomain', 'CallServiceDeskAddress', 'CallServiceDeskCall', 'CallServiceDeskCaller', 'CallServiceDeskCallerDN'];
	var currentPortal = $sp.getPortalRecord();
	var portal = currentPortal.getDisplayValue("url_suffix");

	data.keys = chUtil.getAllConfigKeys('CallServiceDesk');
	data.validateKeys = chUtil.validateKeys(data.keys, validFields);

	//Default values
	data.yammerTitle = 'Vip Concierge';
	data.yammerTitleHtml = 'VIP';
	data.urlYammer = 'ino_ask_it';
	data.techHubTitle = 'Digital Cafe';
	data.sophieChatTitle = 'Chat with Sophie'

	if(portal == 'chameleon_rockwell') {
		data.yammerTitle = 'Viva Engage';
		data.yammerTitleHtml = 'viva<br><b>E</b>';
		data.urlYammer = 'viva_engage';
	}
	else if(portal == 'chameleon_consumers_energy') {
		data.techHubTitle = 'TechHub Jackson';
	}
	else if(portal == 'chameleon_mcdonalds') {
		data.sophieChatTitle = 'Chat with Byte'
	}
	else if(portal == 'chameleon_sephora') {
		data.sophieChatTitle = 'Chat with Rouge'
	}
})();
