sp_widget.client_script:
api.controller = function (spModal, $scope) {
  /* widget controller */
  var c = this

	if(!c.data.token)
		return;

  c.server.get({ action: 'inventory' }).then(function (response) {
    c.inventory = response.data.inventory;
		c.inventoryLoaded = true;
		c.graph.drivesUsageLoaded = true;

    if (!c.inventory)
      return;

    //Local drives usage Graph
    var drivesUsageList = c.inventory.directorysizes
      ? c.inventory.directorysizes.sort(function (a, b) {
          return a.CURVAL - b.CURVAL
        })
      : []

    var drivesUsageOption = buildOptionGraph('yBar', {
      title: response.data.title,
      xName: response.data.descGraph,
      formatter: '{b}: <br/><strong>{c} GB</strong>',
      values: drivesUsageList.map(function (o) {
        return o.DIRTYPE
      }),
      series: [
        {
          type: 'bar',
          data: drivesUsageList.map(function (o) {
            return kbToGb(o.CURVAL)
          }),
        },
      ],
    })

		buildGraph('drivesUsageGraph', drivesUsageOption)
  })

  c.server.get({ action: 'health-score' }).then(function (response) {
    c.healthScore = response.data.healthScore;
		c.scores = response.data.scores;

    c.healthScoreLoaded = true;
		c.graph.healthScoreLoaded = true;
		c.graph.impactLoaded = true;

    if (!c.scores)
      return;

    //Health Score Trend Graph
    var healthScoreOption = buildOptionGraph('line', {
      title: response.data.title,
      legends: [],
      values: c.scores.map(function (o) {
        return formatLabelGraph(new Date(new Date(o.buckettime).toISOString().slice(0, -1)))
      }),
      series: [
        {
          name: response.data.descGraph,
          type: 'line',
          data: c.scores.map(function (o) {
            return (o.healthscore * 100).toFixed(2)
          }),
          markLine: {
            label: { distance: [5, -5] },
            data: [
              {
                name: response.data.averageGraph,
                type: 'average',
                label: { formatter: '{b}: {c}', position: 'insideStartTop' },
              },
            ],
          },
        },
      ],
    });
		buildGraph('healthScoreGraph', healthScoreOption)

    //Total Impact Graph
    var impactOption = buildOptionGraph('xBar', {
      title: response.data.impTitle,
      yName: response.data.impDescGraph,
      valueFormatter: function (value) {
        return minToTime(value)
      },
      axisLabelFormatter: function (value) {
        return value / 1000 + 'k'
      },
      values: response.data.totalImpact.slice(0, 5).map(function (o) {return o.name}),
      series: [
        {
          type: 'bar',
          data: response.data.totalImpact.slice(0, 5).map(function (o) {return o.value}),
        },
      ],
    });
		buildGraph('impactGraph', impactOption)
  })

  c.logon = { starttime: '-' };
  c.server.get({ action: 'logon-process' }).then(function (response) {
    c.logonLoaded = true;
		c.logon = response.data.logon;
  });

	c.graph = {};
  c.server.get({ action: 'cpu-memory-graph' }).then(function (response) {
    var graphs = response.data.graph;
		var cpuMemorySeries = [];
		c.graph.cpuAndMemoryLoaded = true;

    if (!graphs && !graphs.cpu && !graphs.memory)
      return;

		c.graph = Object.assign({}, c.graph, graphs); 

		if (graphs.cpu) {
			cpuMemorySeries.push({
          name: graphs.cpu.ctr,
          data: graphs.cpu.data.map(function (o) {return o.val.toFixed(2)}),
          type: 'line'
        });
		}

		if (graphs.memory) {
			cpuMemorySeries.push({
          name: graphs.memory.ctr,
          data: graphs.memory.data.map(function (o) {return o.val.toFixed(2)}),
          type: 'line'
        });
		}

    //CPU and Memory Graph
    var cpuMemoryOption = buildOptionGraph('line', {
      title: response.data.title,
      legends: [graphs.cpu.ctr, graphs.memory.ctr],
      values: graphs.cpu.data.map(function (o) {return formatLabelGraph(o.time)}),
      series: cpuMemorySeries
    });

    buildGraph('cpuMemoryGraph', cpuMemoryOption);
  })

  c.server.get({ action: 'disk-graph' }).then(function (response) {
    var graphs = response.data.graph;
		var diskSeries = [];
		c.graph.diskLoaded = true;

    if (!graphs && !graphs.diskRead && !graphs.diskWrite)
      return;

		c.graph = Object.assign({}, c.graph, graphs); 

		if(graphs.diskRead) {
			diskSeries.push({
          name: graphs.diskRead.ctr,
          data: graphs.diskRead.data.map(function (o) {return o.val.toFixed(2)}),
          type: 'line',
        });
		}

		if(graphs.diskWrite) {
			diskSeries.push({
          name: graphs.diskWrite.ctr,
          data: graphs.diskWrite.data.map(function (o) {return o.val.toFixed(2)}),
          type: 'line',
        });
		}

    //Disk Graph
    var diskOption = buildOptionGraph('line', {
      title: response.data.title,
      legends: [graphs.diskRead.ctr, graphs.diskWrite.ctr],
      values: graphs.diskRead.data.map(function (o) {return formatLabelGraph(o.time)}),
      series: diskSeries
    });
		buildGraph('diskGraph', diskOption);
  })

  c.topProcessesList = [];
  c.server.get({ action: 'top-processes-applications' }).then(function (response) {
    c.blackbox = response.data.blackbox;
		c.topProcessLoaded = true;

    if (!c.blackbox)
      return;

    c.topProcessesList = response.data.blackbox.applications
      .filter(function (item) {return item.processdata})
      .sort(function (a, b) {
        var prop = $scope.selectedTypeProcess
        if (a.processdata[prop] > b.processdata[prop]) return -1;
        if (a.processdata[prop] < b.processdata[prop]) return 1;
        return 0;
      })
      .slice(0, 7);
  });

	$scope.loadingHTML = function(loaded, value) {
		return loaded ? value || 'N/A'
			: '<div class="loading"><div></div><div></div><div></div><div></div></div>';
	};

	$scope.hasGraphs = function() {
		if(!c.graph.drivesUsageLoaded ||
			!c.graph.healthScoreLoaded ||
			!c.graph.cpuAndMemoryLoaded ||
			!c.graph.diskLoaded ||
			!c.topProcessLoaded)
			return true;

		return (c.inventory && c.inventory.directorysizes) || 
			c.scores || 
			c.graph.cpu || 
			c.graph.memory || 
			c.graph.diskRead || 
			c.graph.diskWrite ||
			c.blackbox;
	}

  $scope.setPercentColor = function (param) {
    if (param === '-') {
      return 'grey';
    }

    var percent = param * 100;

    switch (true) {
      case percent < 40 || percent == 'red':
        return '#D3470E'
      case percent < 60 || percent == 'yellow':
        return '#F0A800'
      default:
        return '#027D5B'
    }
  }

  $scope.getTypeProcessValue = function () {
    switch ($scope.selectedTypeProcess) {
      case 'cpu':
        return '%';
      case 'netTraf':
        return 'Bytes/sec';
      default:
        return 'MB';
    }
  }

  $scope.updateListTopProcesses = function () {
    if (!$scope.selectedTypeProcess) 
			$scope.selectedTypeProcess = 'cpu';

		if (!c.blackbox)
			return;

    c.topProcessesList = c.blackbox.applications
      .filter(function(item) {return item.processdata})
      .sort(function(a, b) {
        var prop = $scope.selectedTypeProcess
        if (a.processdata[prop] > b.processdata[prop]) return -1;
        if (a.processdata[prop] < b.processdata[prop]) return 1;
        return 0;
      })
      .slice(0, 7)
  }

  function formatAMPM(date) {
    var month = addLeftZero(date.getMonth() + 1);
    var day = addLeftZero(date.getDate());
    var hours = date.getHours();
    var minutes = addLeftZero(date.getMinutes());

    if (hours == 0 && date.getMinutes() == 0) 
			return month + '/' + day;

    var ampm = hours >= 12 ? 'PM' : 'AM';
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    return month + '/' + day + ' ' + hours + ':' + minutes + ' ' + ampm;
  }

  function formatLabelGraph(value) {
    return {
      value: formatAMPM(new Date(value)),
      textStyle: {
        fontSize: '0.8rem',
        fontWeight: 'lighter',
      },
    }
  }

  function addLeftZero(value) {
    return value < 10 ? '0' + value : value;
  }

  function kbToGb(value) {
    return (value / 1073741824).toFixed(2);
  }

  function minToTime(value) {
    var timeHours = value / 60;
    var days = Math.trunc(timeHours / 24);
    var hours = Math.floor(timeHours - days * 24);
    var min = Math.trunc((timeHours - Math.floor(timeHours)) * 60);
    return (
      (days > 0 ? days + ' Day' + (days > 1 ? 's' : '') : '') +
      (hours > 0 ? (days > 0 ? (min > 0 ? ', ' : ' and ') : '') + hours + ' Hour' + (hours > 1 ? 's' : '') : '') +
      (min > 0 ? (hours > 0 ? ' and ' : '') + min + ' Minute' + (min > 1 ? 's' : '') : '')
    )
  }

  function buildGraph(id, option) {
    var theChart = echarts.init(document.getElementById(id));

    theChart.setOption(option);

    window.addEventListener('resize', function () {
      theChart.resize()
    });
  }

  function buildOptionGraph(type, prop) {
    var option = {
      textStyle: { fontWeight: 'lighter', color: '#999', fontFamily: 'Arial' },
      title: {
        text: prop.title,
        textStyle: { fontSize: '1.8rem', fontWeight: 'lighter', color: '#999', fontFamily: 'Arial' },
      },
      tooltip: { trigger: 'axis' },
      legend: { right: 0, textStyle: { fontSize: 10 } },
      grid: { left: '5%', right: '4%', bottom: '5%', containLabel: true },
      visualMap: [
        { show: false, seriesIndex: 0, type: 'continuous', min: 0, color: ['#004ba4', '#79c8ff'] },
        { show: false, seriesIndex: 1, type: 'continuous', min: 0, color: ['#00bc3a', '#79ffa2'] },
      ],
      series: prop.series
    }

    switch (type) {
      case 'line':
        option.xAxis = { type: 'category', boundaryGap: false, data: prop.values }
        option.yAxis = { type: 'value', max: 'dataMax' }
        break
      case 'yBar':
        option.xAxis = { type: 'value', nameLocation: 'middle', nameGap: 25 }
        option.yAxis = { type: 'category', boundaryGap: true, axisLabel: { interval: 0 }, data: prop.values }
        break
      case 'xBar':
        option.xAxis = { type: 'category', boundaryGap: true, axisLabel: { interval: 0 }, data: prop.values }
        option.yAxis = { type: 'value', nameLocation: 'middle', nameGap: 25 }
        break
    }

    if (prop.legends) option.legend.data = prop.legends;
    if (prop.xName) option.xAxis.name = prop.xName;
    if (prop.yName) option.yAxis.name = prop.yName;
    if (prop.formatter) option.tooltip.formatter = prop.formatter;
    if (prop.valueFormatter) option.tooltip.valueFormatter = prop.valueFormatter;
    if (prop.axisLabelFormatter) option.yAxis.axisLabel = { formatter: prop.axisLabelFormatter };

    return option;
  }
}

