sp_widget.script:
(function() {
	//Get current user's email
	var gr = new GlideRecord('sys_user');
	gr.get(gs.getUserID());
	//var userEmail = gr.email;
	var userEmail = 'vijaykumar.basu@stefanini.com';

	var cacheKey = 'ithealth-systrack-device-xray-' + userEmail;
	var cacheExpiryTime = 1200000; //2 minutes
	var cacheLimitExpiryTime = 604800000; //7 days
	var compareCacheTime = new Date().getTime() - cacheExpiryTime;
	var limitCacheTime = new Date().getTime() - cacheLimitExpiryTime;

	//It will just follow if it has no cache or is expired.
	var dateScore = new Date(new Date().setHours(new Date().getHours() -3)).toISOString();
	var startDate12h = new Date(new Date().setHours(new Date().getHours() -12)).toISOString();
	var startDate24h = new Date(new Date().setHours(new Date().getHours() -24)).toISOString();
	var startDate30d = new Date(new Date().setDate(new Date().getDate() -30)).toISOString();
	var endDate = new Date().toISOString();

	//Get token to authenticate to SysTrack
	data.token = cacheCallbackResult(cacheKey + '-auth', function () {
    var request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'Authentication');
		var req = getRequest(request);
    return req && req.access_token || null;
  });

	if(!data.token)
		return;

	//Get resume logged user data
  data.user = cacheCallbackResult(cacheKey + '-user', function () {
    request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'User');
    request.setStringParameterNoEscape('accessToken', data.token);
    request.setStringParameterNoEscape('email', userEmail);
  	var req = getRequest(request);
    var internalUser = req ? req.data[0] : null;

    if (!internalUser)
      return null;

    internalUser.username = internalUser.username.replace(/\\\\/g,'\\');

    request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'System User by Login');
    request.setStringParameterNoEscape('accessToken', data.token);
    request.setStringParameterNoEscape('username', internalUser.username);
		req = getRequest(request);
    var systemUserByLogin = req ? req.data[0] : null;

    systemUserByLogin.account = internalUser.account;
    systemUserByLogin.email = internalUser.email;
    systemUserByLogin.fullname = internalUser.fullname;
    systemUserByLogin.upn = internalUser.upn;

    return systemUserByLogin;
  });

  if (!input || !data.user)
    return;

  //Get SysTrack Inventory
  if (input.action === 'inventory') {
    data.inventory = null;
		data.title = gs.getMessage("Local Drives Usage");
		data.descGraph = gs.getMessage("Directory Sizes (GB)");

    var inventoryClasses = 'system:networkinterface:memory:directorysizes';
    data.inventory = cacheCallbackResult(cacheKey + '-inventory', function () {
      request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'Inventory');
      request.setStringParameterNoEscape('accessToken', data.token);
      request.setStringParameterNoEscape('userGuid', data.user.guid);
      request.setStringParameterNoEscape('classes', inventoryClasses);
      var req = getRequest(request);

      return req ? req.data[0] : [];
    });

    return;
  }

  //Get SysTrack Health Score
  if (input.action === 'health-score') {
    data.scores = null;
    data.healthScore = null;
    data.title = gs.getMessage("Health Score Trend");
		data.descGraph = gs.getMessage("Health Score");
		data.averageGraph = gs.getMessage("Average Health Score");
		data.impTitle = gs.getMessage("Total Impact");
		data.impDescGraph = gs.getMessage("Minutes");

    var cachedResult = cacheCallbackResult(cacheKey + '-health-score', function () {
      request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'Health Score');
      request.setStringParameterNoEscape('accessToken', data.token);
      request.setStringParameterNoEscape('userGuid', data.user.guid);
      request.setStringParameterNoEscape('startDate', startDate30d);
      req = getRequest(request);

      var totalImpact = [];
      var result = {
        totalImpact: totalImpact
      };

      function updateImpactValues(key, param, obj) {
        var index = totalImpact.map(function(o) { return o.name; }).indexOf(key);
        if(index < 0) 
          totalImpact.push({'name': key, 'value': obj[param]});
        else 
          totalImpact[index].value = totalImpact[index].value + obj[param];
      }      

      result.scores = req ? req.data : null;

      if(result.scores) {
        result.healthScore = ((result.scores[result.scores.length - 1]).healthscore * 10).toFixed(1);
        result.scores.forEach(function(item) {
          updateImpactValues('CPU', 'cpucostsum', item);
          updateImpactValues('Disk', 'diskcostsum', item);
          updateImpactValues('Memory', 'memcostsum', item);
          updateImpactValues('Network', 'netcostsum', item);
          updateImpactValues('Latency', 'latencycostsum', item);
          updateImpactValues('Startup', 'startupcostsum', item);
          updateImpactValues('Virtual Memory', 'vmcostsum', item);
          updateImpactValues('Virtual Machine', 'virtcostsum', item);
          updateImpactValues('Software Install', 'installcostsum', item);
          updateImpactValues('Software Update', 'updatecostsum', item);
          updateImpactValues('Events', 'eventcostsum', item);
          updateImpactValues('Faults', 'faultcostsum', item);
          updateImpactValues('Hardware', 'hwproblemcostsum', item);
        });

        result.totalImpact.sort(function(a, b){return b.value-a.value});
      }
      return result;
    });

    data.scores = cachedResult.scores;
    data.healthScore = cachedResult.healthScore;
    data.totalImpact = cachedResult.totalImpact;
    return;
  }

	//Get Systrack Logon Process
  if (input.action === 'logon-process') {

    data.logon = cacheCallbackResult(cacheKey + '-logon-process', function () {
      request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'Logon Process');
      request.setStringParameterNoEscape('accessToken', data.token);
      request.setStringParameterNoEscape('startTime', endDate);
      request.setStringParameterNoEscape('userGuid', data.user.guid);
      req = getRequest(request);

      return req ? req.data[0].applications[0] : null;
    });

    return;
  }

  data.graph = {};

  if (input.action === 'cpu-memory-graph') {
		data.title = gs.getMessage("CPU and Memory");

    //Get CPU Graph
    var cpuGraphId = 80;
    data.graph.cpu = cacheCallbackResult(cacheKey + '-cpu-graph', function () { 
      return getGraphData(cpuGraphId, null);
    });

	  //Get Memory Graph
    var memoryGraphId = 265;
    data.graph.memory = cacheCallbackResult(cacheKey + '-memory-graph', function () { 
      return getGraphData(memoryGraphId, null);
    });

    return;
  }

  if (input.action === 'disk-graph') {
		data.title = gs.getMessage("Disk");

    //Get Disk Read Graph
    var diskReadGraphId = 294;
    data.graph.diskRead = cacheCallbackResult(cacheKey + '-disk-read-graph', function () { 
      return getGraphData(diskReadGraphId, 'C');
    });

	  //Get Disk Write Graph
    var diskWriteGraphId = 295;
    data.graph.diskWrite = cacheCallbackResult(cacheKey + '-disk-write-graph', function () { 
      return getGraphData(diskWriteGraphId, 'C');
    });

    return;
  }

	//Get Top Processes of Applications
  if (input.action === 'top-processes-applications') {
    var blackBoxProperties = 'applications';
    data.blackbox = cacheCallbackResult(cacheKey + '-top-processes-applications', function () { 
      request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'BlackBox');
      request.setStringParameterNoEscape('accessToken', data.token);
      request.setStringParameterNoEscape('userGuid', data.user.guid);
      request.setStringParameterNoEscape('dateTime', endDate);
      request.setStringParameterNoEscape('properties', blackBoxProperties);
      var req = getRequest(request);

      return req ? req.data[0] : null;
    });

    return;
  }

	function setUserCache(key, value) {
		const item = {
			value: value,
			registeredTime: new Date().getTime()
		}
		gs.getSession().putClientData(key, JSON.stringify(item));
		return item;
	}

	function cacheCallbackResult(key, callback) {
		var cachedValueJson = gs.getSession().getClientData(key);
		var cacheValue = JSON.parse(cachedValueJson);

		if(cacheValue) {
			//...and is less than 2 minutes, give that back.
			if(cacheValue.registeredTime > compareCacheTime) {
				return cacheValue.value;
			}

			// Limit time to clear cache
			if(cacheValue.registeredTime < limitCacheTime) {
				setUserCache(key, {});
			}
		}

		var valueToBeCached = callback();

		setUserCache(key, valueToBeCached);

		return valueToBeCached
	}

	function getRequest(req) {
		try {
			var response = req.execute();
			return response.getStatusCode() == 200 ? JSON.parse(response.getBody()) : null;
		} catch(ex){
			$sp.log('Get Request - ' + ex.getMessage());
			return null;
		}
	}

	function getGraphData(graphId, instance) {
		var request = new sn_ws.RESTMessageV2('x_stefa_chameleon.D3 Lakeside Systrack', 'Graphing');
		request.setStringParameterNoEscape('accessToken', data.token);
		request.setStringParameterNoEscape('userGuid', data.user.guid);
		request.setStringParameterNoEscape('startTime', startDate12h);
		request.setStringParameterNoEscape('endTime', endDate);
		request.setStringParameterNoEscape('graphId', graphId);
		if(instance)
			request.setStringParameterNoEscape('instance', instance);

		var req = getRequest(request);
		return req ? req.data[0] : null;
	}
})();
