sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var gr = new GlideRecord('sys_user');
	gr.addQuery('department', '=', options.hr_department || '93b25282c0a8000b0b55c8ab34e2f1e6');
	gr.query();

	var results = [];

	while (gr.next()) {
		results.push({
			name: gr.getValue('name'),
			title: gr.getValue('title'),
			email: gr.getValue('email'),
			time_zone: gr.getValue('time_zone'),
			photo: gr.getDisplayValue('photo'),
			phone: gr.getValue('phone'),
			mobile_phone: gr.getValue('mobile_phone'),
		});
	}

	data.users = results;
})();
