sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.title = gs.getMessage(options.title);
	data.rating_description = gs.getMessage(options.rating_description);
	data.description = gs.getMessage(options.description);
	data.input_placeholder = gs.getMessage(options.input_placeholder);
	data.button_text_open = gs.getMessage(options.button_text_open);
	data.button_text_closed = gs.getMessage(options.button_text_closed);
	data.button_text_send_again = gs.getMessage(options.button_text_send_again);

	var localInput = input;

	if (!localInput)
		return;

  var url = localInput.url;

	if (localInput.action === 'add-suggestion') {
		var suggestion = localInput.suggestion;
		var sendAnonymous = localInput.sendAnonymously;
		var rating = localInput.rating;
		var location = localInput.location;
		var category = localInput.category;

		var record = new GlideRecord('x_stefa_chameleon_suggestion');
		record.newRecord();
		record.short_description = '[' + location + ']: New Suggestion';
		record.description = suggestion;
		record.assigned_to = options.who_will_be_notified;
		record.u_url = url;
		record.u_rating = rating;
		record.u_isanonymous = sendAnonymous;
		record.u_category = category;

		if (sendAnonymous) {
			record.autoSysFields(false);
		}

		var newId = record.insert();

		if (sendAnonymous) {
			record.initialize();
			record.autoSysFields(false);
			record.get(newId);
			record.opened_by = '';
			record.update();
		}
	}

	if (localInput.action === 'get-state') {
		var alreadySuggested = new GlideRecord('x_stefa_chameleon_suggestion');
		alreadySuggested.addQuery('opened_by', '=', gs.getUserID());
		alreadySuggested.addQuery('u_url', '=', url);
		alreadySuggested.setLimit(1);
		alreadySuggested.query();

		data.alreadySuggested = alreadySuggested.hasNext();
	}
})();
