sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	if (options.image) {
		var imageRecord = new GlideRecord('db_image');

		if (!imageRecord.get(options.image))
			return;

	  data.image_url = imageRecord.getDisplayValue('image');
	}
})();
