sp_widget.script:
(function() {
	options.show_description = (options.show_description === "true");
	options.title = gs.getMessage(options.title || 'Location Performance');

	//Changing titles per Portal
	var currentPortal = $sp.getPortalRecord();
	var portal = currentPortal.getDisplayValue("url_suffix");

	data.RefrigerationWord = 'Refrigerator';
	data.hideRefrigerationUnit = false;

	if(portal == 'chameleon_sephora') {
		data.RefrigerationWord = 'POS';
		data.hideRefrigerationUnit = true;
	}
})();
