sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	var cachedContentKey = 'CACHED_TRAINING';
	var userId = c.data.userId;

	function randomTraining(name, progressBg, progressFill, index) {
		var charCodeAt = userId[index % userId.length].charCodeAt(0);
		var progress = Math.max(charCodeAt % 100, 10);

		return { name: name, progressBg: progressBg, progressFill: progressFill, progress: progress };
	}

	function getTrainings() {
		/*var cachedContent = window.localStorage.getItem(cachedContentKey);

		if (cachedContent) {
			return JSON.parse(cachedContent);
		}*/

		var trainings = [
			randomTraining(
				'${Skills Development: Introduction Training}', 
				'#c1f1ed',
				'#81d8d0',
				1
			),
			randomTraining(
				'${Skills Development: Effective Communication Strategies}', 
				'#a0d6f4',
				'#4bb4ef',
				2
			),
			randomTraining(
				'${Skills Development: Mastering Time Management}', 
				'#da94b7',
				'#910048',
				3
			),
		];

		//window.localStorage.setItem(cachedContentKey, JSON.stringify(trainings));

		return trainings;
	}

	c.trainings = getTrainings();
};

