sp_widget.client_script:
api.controller=function(spModal) {
  /* widget controller */
  var c = this;

	var CACHED_MY_STUFFS = 'CACHED_MY_STUFFS';

	function saveLinks() {
		localStorage.setItem(CACHED_MY_STUFFS, JSON.stringify(c.links));
	}

	function getLinks() {
		var cachedLinks = localStorage.getItem(CACHED_MY_STUFFS);

		if (!cachedLinks) {
			return [
				{ title: 'My Tickets', link: '?id=my_open_tickets' },
				{ title: 'Order Services', link: '?id=sc_home' },
			];
		}

		return JSON.parse(cachedLinks);
	}

	c.remove = function(item) {
		c.links = c.links.filter(function (i) {
			return i !== item;
		});

		saveLinks();
	}

	c.links = getLinks();

	c.addShortcut = function() {
		spModal.prompt("What is your shortcut link?").then(function(link) {
			spModal.prompt("What is the name you want to give this shortcut?").then(function(title) {
				var index = c.links.findIndex(function (i) {
					return i.link === link;
				});

				if (index !== -1) {
					c.links[index].title = title;
					saveLinks();

					return;
				}

				c.links.push({
					title: title,
					link: link,
				});

				saveLinks();
			});
		});
	}
};

