sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	c.containerId = Math.random().toString(16).slice(2);

	setTimeout(function () {
		var chartDom = document.getElementById(c.containerId);
		var myChart = echarts.init(chartDom);
		var option = {
			series: [
				{
					type: 'gauge',
					axisLine: {
						lineStyle: {
							width: 70,
							color: [
								[0.5, '#d91221'],
								[0.7, '#f08302'],
								[0.9, '#feba02'],
								[1, '#0f7c10']
							]
						}
					},
					axisTick: {
						distance: 0,
						length: 8,
						lineStyle: {
							color: '#fff',
							width: 0
						}
					},
					splitLine: {
						distance: -80,
						length: 0,
						lineStyle: {
							color: '#fff',
							width: 0
						}
					},
					axisLabel: {
						color: 'inherit',
						distance: 40,
						fontSize: 20
					},
					detail: {
						valueAnimation: true,
						formatter: '{value}',
						color: 'black',
						fontSize: '2rem',
					},
					data: [
						{
							value: 78.95
						}
					]
				}
			]
		};

		myChart.setOption(option);
	}, 10);
};

sp_widget.css:
.dl-body  {

  h2 {
  	text-align: center;
    margin-top: 0;

    font-size: 2.5rem;
  }

  & > div {
    min-height: 300px;
    min-width: 200px;

    height: 100%;
    width: 100%;
  }
}
