sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	var chart1 = document.getElementById('ino_dow_manager_chart_1');
	echarts.init(chart1).setOption({
		tooltip: {
			trigger: 'item'
		},
		legend: {
			top: '5%',
			left: 'center',
			// doesn't perfectly work with our tricks, disable it
			selectedMode: false
		},
		series: [
			{
				name: 'Ticket Status',
				type: 'pie',
				radius: ['40%', '70%'],
				center: ['50%', '70%'],
				// adjust the start angle
				startAngle: 180,
				label: {
					show: true,
					formatter: function(param) {
						// correct the percentage
						return param.name + ' (' + param.percent * 2 + '%)';
					}
				},
				data: [
					{ value: 0, name: 'Pending', itemStyle: { color: '#5470c6' } },
					{ value: 2, name: 'Open', itemStyle: { color: '#73c0de' } },
					{ value: 3, name: 'Working in Progress', itemStyle: { color: '#fac858' } },
					{ value: 8, name: 'Closed Complete', itemStyle: { color: '#3ba272' } },
					{ value: 0, name: 'Closed Incomplete', itemStyle: { color: '#ee6666' } },
					{ value: 0, name: 'Closed Skipped', itemStyle: { color: 'gray' } },
					{
						// make an record to fill the bottom 50%
						value: 13,
						itemStyle: {
							// stop the chart from rendering this piece
							color: 'none',
							decal: {
								symbol: 'none'
							}
						},
						label: {
							show: false
						}
					}
				]
			}
		]
	});

	var chart2 = document.getElementById('ino_dow_manager_chart_2');
	echarts.init(chart2).setOption({
		tooltip: {
			trigger: 'item'
		},
		legend: {
			top: '5%',
			left: 'center'
		},
		series: [
			{
				name: 'Personas',
				type: 'pie',
				radius: ['20%', '40%'],
				label: {
					show: true,
					formatter: function(param) {
						// correct the percentage
						return param.name + ' (' + param.percent * 2 + '%)';
					}
				},
				data: [
					{ value: 30, name: 'Onboarding Users' },
					{ value: 8, name: 'Managers' },
					{ value: 4, name: 'RH' },
					{ value: 10, name: 'IT' },
				]
			}
		]
	});

	var chart3 = document.getElementById('ino_dow_manager_chart_3');
	echarts.init(chart3).setOption({
		xAxis: {
			type: 'category',
			data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
		},
		yAxis: {
			type: 'value'
		},
		series: [
			{
				label: {
					show: true,
					formatter: function(param) {
						return param.value;
					}
				},
				data: [30, 10, 40, 60, 80, 2, 0],
				type: 'bar'
			}
		]
	});

	var chart4 = document.getElementById('ino_dow_manager_chart_4');
	echarts.init(chart4).setOption({
		xAxis: {
			type: 'category',
			data: ['JAN', 'FEB', 'MAR', 'APR', 'MAY', 'JUN']
		},
		yAxis: {
			type: 'value'
		},
		series: [
			{
				data: [63, 72, 51, 62, 63, 53],
				type: 'bar'
			}
		]
	});
};

