sp_widget.script:
var aisEnabled = $sp.isAISearchEnabled();

if (aisEnabled)
	data.typeAheadSearch = $sp.getWidget('typeahead-search', options);
else
	data.typeAheadSearch = $sp.getWidget('typeahead-search', options.typeahead_search); 

var user = new GlideRecord('sys_user');
user.get(gs.getUserID());

var userName = user.getValue('name');
var firstName = (userName || '').split(' ')[0];

data.avatar = user.getDisplayValue('avatar');
data.userName = userName;

if (options.title)
  data.title = gs.getMessage(options.title).replace('{{name}}', firstName);
else
	data.title = gs.getMessage('Hello {0}, how can we help?', firstName);
