sp_widget.client_script:
api.controller=function(spModal) {
	function buildCircleProgressBar(id, progress, text, color, fontSize) {
		var option = {
			color: [color, '#d4d4d4'],
			title: {
				 text: text,
				 left: 'center',
				 top: 'center',
				 textStyle: {
					 color: color,
					 fontSize: fontSize
				 },
			},
			tooltip: {
				trigger: 'item'
			},
			legend: {
				top: '5%',
				left: 'center',
				show: false
			},
			series: [
				{
					name: 'Progress Bar',
					type: 'pie',
					radius: ['50%', '70%'],
					avoidLabelOverlap: false,
					label: {
						show: false
					},
					tooltip: {
						show: false
					},
					emphasis: {
						label: {
							show: false
						}
					},
					labelLine: {
						show: false
					},
					data: [
						{ value: progress, name: '0_Fill' },
						{ value: 100 - progress, name: '1_Empty' },
					]
				}
			]
		}

		var theChart = echarts.init(
			document.getElementById(id)
		);

		theChart.setOption(option);

		window.addEventListener('resize', function() {
			theChart.resize();
		});
	}

	/* widget controller */
  var c = this;

	if (!c.data.userCache || !c.data.userCache.value)
		return;

	var healthScore = getFixed(c.data.userCache.value.scores.healthscore * 100);

	setTimeout(function () {
		buildCircleProgressBar('healthScoreIndicator', healthScore,  healthScore + '%', setPercentColor(healthScore), 20);

		c.data.userCache.value.sensors.slice(0,3).forEach(function(sensor) {
			var targetToPercent = sensor.target ? getFixed((sensor.main_value / sensor.target) * 100) : 100;
			buildCircleProgressBar(sensor.sensor_id, targetToPercent, getFixed(sensor.main_value), setPercentColor(sensor.icon_color), 14);
		});
	}, 10);

	function getFixed(value) {
		if(typeof value === 'number' &&
    	!Number.isNaN(value) &&
    	!Number.isInteger(value)) {
			return (value || 0).toFixed(1);
		}
		return (value || null);
	}

	function setPercentColor(param) {
		switch(true){
			case (param < 40 || param == 'red'):
				return '#D3470E';
			case (param < 60 || param == 'yellow'):
				return '#F0A800';
			default:
				return '#027D5B';
		}
	}

	c.seeDetails = function() {
		spModal.open({
			title: 'IT Health Details',
			widget: 'it_health_systrack_details', 
			shared: c.data.userCache.value,
			size: 'lg',
			buttons: [{label:'Close', cancel: true}]
		});
	}
};

