sp_widget.client_script:
api.controller=function($scope, $sce) {
  /* widget controller */
  var c = this;

	c.html = $sce.trustAsHtml(c.options.html);
	$scope.$watch('c.options.html',function(){
		c.html = $sce.trustAsHtml(c.options.html);
	});
};

