sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.title = gs.getMessage(options.title);

	var image = new GlideRecord('db_image');

	if (image.get(options.image))
		data.image_url = image.getDisplayValue('image');
})();
