sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.css:
p {
	max-width: 100%;
  white-space: pre-wrap;
  word-wrap: break-word;
}

sp_widget.script:
(function() {
	function loadCriteriaNames(listCriteriaIds) {
		var query = new GlideRecord('user_criteria');
		var list = [];

		query.addQuery('sys_id', 'IN', listCriteriaIds);
		query.query();

		while (query.next()) {
			list.push(query.getDisplayValue('name'));
		}

		return list;
	}

	data.userCriterias = loadCriteriaNames(
		sn_uc.UserCriteriaLoader.getAllUserCriteria(gs.getUserID()),
	);

	options.basic_level_personas = options.basic_level_personas || '';
	options.medium_level_personas = options.medium_level_personas || '';
	options.great_level_personas = options.great_level_personas || '';

	data.isBasicLevel = sn_uc.UserCriteriaLoader.userMatches(gs.getUserID(), options.basic_level_personas.split(','));
	data.isMediumLevel = sn_uc.UserCriteriaLoader.userMatches(gs.getUserID(), options.medium_level_personas.split(','));
	data.isGreatLevel = sn_uc.UserCriteriaLoader.userMatches(gs.getUserID(), options.great_level_personas.split(','));

	data.basicMatchingCriterias = loadCriteriaNames(
		sn_uc.UserCriteriaLoader.getMatchingCriteria(gs.getUserID(), options.basic_level_personas.split(','))
	);
	data.mediumMatchingCriterias = loadCriteriaNames(
		sn_uc.UserCriteriaLoader.getMatchingCriteria(gs.getUserID(), options.medium_level_personas.split(','))
	);
	data.greatMatchingCriterias = loadCriteriaNames(
		sn_uc.UserCriteriaLoader.getMatchingCriteria(gs.getUserID(), options.great_level_personas.split(','))
	);
})();
