sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	c.widgetId = 'swiper_' + Math.random().toString(16).slice(2);
	var links = (c.options.redirect_links || '').split(',');

	c.articles = [
		{
		  image: '/x_stefa_chameleon.dow_mock_article_1.png',
			description: 'Employee Handbook:\nOur comprehensive handbook provides you with all the  essential information about our company\'s...',
			href: links[0] || '#',
		},
		{
		  image: '/x_stefa_chameleon.dow_mock_article_2.png',
			description: 'Training Modules:\nAccess our interactive training modules designed to equip you with the necessary skills...',
			href: links[1] || '#',
		},
		{
		  image: '/x_stefa_chameleon.dow_mock_article_3.png',
			description: 'Mentorship Program:\nBenefit from our mentorship program, where experienced employees ...',
			href: links[2] || '#',
		},
		{
		  image: '/x_stefa_chameleon.dow_mock_article_4.png',
			description: 'Company Culture Events:\nEngage in various company culture events, including team-building activities...',
			href: links[3] || '#',
		},
		{
		  image: '/x_stefa_chameleon.dow_mock_article_5.png',
			description: 'Leadership Development Program:\nA leadership development program equips employees with the necessary...',
			href: links[4] || '#',
		}
	];

	setTimeout(function () {
		var swiperEl = document.getElementById(c.widgetId);

    Object.assign(swiperEl, {
      slidesPerView: 1,
      spaceBetween: 10,
			navigation: {
				nextEl: '.swiper-button-next-unique',
				prevEl: '.swiper-button-prev-unique'
			},
      breakpoints: {
        640: {
          slidesPerView: 2,
          spaceBetween: 20,
        },
        1024: {
          slidesPerView: 4,
          spaceBetween: 20,
        },
      },
    });
    swiperEl.initialize();
	}, 100);
};

