sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function() {  
	/* populate the 'data' object */  /* e.g., data.table = $sp.getValue('table'); */
	function loadCriteriaNames(listCriteriaIds) {
		var query = new GlideRecord('user_criteria');
		var list = [];

		query.addQuery('sys_id', 'IN', listCriteriaIds);
		query.query();

		while (query.next()) {
			list.push(query.getDisplayValue('name'));
		}

		return list;
	}

	data.personas = loadCriteriaNames(
		new sn_uc.UserCriteriaLoader.getAllUserCriteria(gs.getUserID())
	).join(', ');
})();
