// Insert sensors in the IT Health Sensors table
sensors.forEach(function(current) {
	gr.initialize();

	if (gr.get('sensor_id', current.sensor_id))
		return;

	gr.sensor_name = current.sensor_name;
	gr.sensor_id = current.sensor_id;
	gr.priority = current.priority;
	gr.main_variable = current.main_variable;
	gr.target = current.target;
	gr.title = current.title;
	gr.icon_color = current.icon_color;
	gr.icon_image = current.icon_image;

	gr.insert();
});
